mapper.select:

SELECT GROUPNO, GROUPORD, DEPTH, RNO, REPLY, REPLYER, REGDATE
FROM 
	(
		SELECT /*+ INDEX_DESC(TEST_REPLY PK_TEST_REPLY) */ 
		ROWNUM RN, GROUPNO, GROUPORD, DEPTH, RNO, REPLY, REPLYER, REGDATE
		FROM TEST_REPLY
		WHERE BNO = #{bno}
		AND RNO > 0 AND ROWNUM <= #{cri.pageNum} * #{cri.amount}
	)
WHERE RN > (#{cri.pageNum} - 1) * #{cri.amount}

mapper.update:
UPDATE TEST_REPLY
SET REPLY = #{reply}
WHERE RNO = #{rno}
